(301, '_transient__eg_addons', 'a:30:{i:0;O:8:"stdClass":6:{s:5:"title";s:12:"Albums Addon";s:4:"slug";s:13:"envira-albums";s:7:"version";s:5:"1.7.2";s:5:"image";s:69:"https://enviragallery.com/wp-content/uploads/2015/10/albums-addon.png";s:7:"excerpt";s:178:"Give your galleries a place to call home with the Albums addon. Create albums, add galleries, manage cover photos, show gallery titles and even image counts in this superb addon!";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:1;O:8:"stdClass":6:{s:5:"title";s:9:"CSS Addon";s:4:"slug";s:10:"envira-css";s:7:"version";s:5:"1.3.3";s:5:"image";s:66:"https://enviragallery.com/wp-content/uploads/2015/10/css-addon.png";s:7:"excerpt";s:149:"Place your own custom CSS into each individual gallery with the CSS addon, giving you endless possibilities for your gallery style and display views.";s:10:"categories";a:1:{i:0;s:4:"free";}}i:2;O:8:"stdClass":6:{s:5:"title";s:17:"Deeplinking Addon";s:4:"slug";s:18:"envira-deeplinking";s:7:"version";s:5:"1.4.4";s:5:"image";s:74:"https://enviragallery.com/wp-content/uploads/2015/10/deeplinking-addon.png";s:7:"excerpt";s:151:"Get full SEO benefits and granular control over your galleries with the Deeplinking addon. Create unique, indexable URLs for each item in your gallery.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:3;O:8:"stdClass":6:{s:5:"title";s:14:"Defaults Addon";s:4:"slug";s:15:"envira-defaults";s:7:"version";s:5:"1.4.6";s:5:"image";s:71:"https://enviragallery.com/wp-content/uploads/2015/10/defaults-addon.png";s:7:"excerpt";s:113:"The Defaults Addon allows you to easily specify default settings for all new galleries that you create in Envira.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:4;O:8:"stdClass":6:{s:5:"title";s:15:"Downloads Addon";s:4:"slug";s:16:"envira-downloads";s:7:"version";s:5:"1.4.7";s:5:"image";s:72:"https://enviragallery.com/wp-content/uploads/2016/05/downloads-addon.png";s:7:"excerpt";s:102:"Give your users the ability to download your images and galleries with an easy to use download button.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:5;O:8:"stdClass":6:{s:5:"title";s:22:"Dropbox Importer Addon";s:4:"slug";s:23:"envira-dropbox-importer";s:7:"version";s:5:"1.3.1";s:5:"image";s:70:"https://enviragallery.com/wp-content/uploads/2015/10/dropbox-addon.png";s:7:"excerpt";s:140:"The Dropbox Importer Addon for Envira Gallery allows you to import images from your Dropbox account, straight in to your Envira Galleries.\r\n";s:10:"categories";a:1:{i:0;s:4:"free";}}i:6;O:8:"stdClass":6:{s:5:"title";s:13:"Dynamic Addon";s:4:"slug";s:14:"envira-dynamic";s:7:"version";s:5:"1.5.4";s:5:"image";s:70:"https://enviragallery.com/wp-content/uploads/2015/10/dynamic-addon.png";s:7:"excerpt";s:108:"The Dynamic Addon is a powerful Envira Addon that lets you create dynamic and powerful galleries on the fly.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:7;O:8:"stdClass":6:{s:5:"title";s:10:"EXIF Addon";s:4:"slug";s:11:"envira-exif";s:7:"version";s:5:"1.4.4";s:5:"image";s:67:"https://enviragallery.com/wp-content/uploads/2015/10/exif-addon.png";s:7:"excerpt";s:108:"The EXIF Addon for Envira Gallery allows you to display EXIF metadata in your galleries and lightbox images.";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:8;O:8:"stdClass":6:{s:5:"title";s:22:"Featured Content Addon";s:4:"slug";s:23:"envira-featured-content";s:7:"version";s:5:"1.2.6";s:5:"image";s:79:"https://enviragallery.com/wp-content/uploads/2016/05/featured_content_addon.png";s:7:"excerpt";s:109:"Easily create WordPress galleries based on posts, pages, products, testimonials, and other custom post types.";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:9;O:8:"stdClass":6:{s:5:"title";s:16:"Fullscreen Addon";s:4:"slug";s:17:"envira-fullscreen";s:7:"version";s:5:"1.2.2";s:5:"image";s:73:"https://enviragallery.com/wp-content/uploads/2015/10/fullscreen-addon.png";s:7:"excerpt";s:173:"Take your gallery to the next and biggest viewing environment with the Fullscreen addon. Blow up your images as large as they can be with the native fullscreen browser mode.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:10;O:8:"stdClass":6:{s:5:"title";s:20:"Gallery Themes Addon";s:4:"slug";s:21:"envira-gallery-themes";s:7:"version";s:5:"1.4.8";s:5:"image";s:74:"https://enviragallery.com/wp-content/uploads/2015/10/gallery-templates.png";s:7:"excerpt";s:87:"Enhance the appearance of your gallery layout with beautiful and custom gallery themes!";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:11;O:8:"stdClass":6:{s:5:"title";s:15:"Instagram Addon";s:4:"slug";s:16:"envira-instagram";s:7:"version";s:5:"1.4.4";s:5:"image";s:72:"https://enviragallery.com/wp-content/uploads/2015/08/instagram-addon.png";s:7:"excerpt";s:169:"Showcase your Instagram photos in your WordPress galleries. Envira''s Instagram addon allows you to import your images from Instagram into WordPress with 3 simple clicks.";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:12;O:8:"stdClass":6:{s:5:"title";s:15:"Lightroom Addon";s:4:"slug";s:16:"envira-lightroom";s:7:"version";s:5:"2.2.3";s:5:"image";s:72:"https://enviragallery.com/wp-content/uploads/2016/01/lightroom-addon.png";s:7:"excerpt";s:150:"The Lightroom Addon for Envira Gallery allows you to automatically create and sync photo galleries from your Adobe Lightroom collections in WordPress.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:13;O:8:"stdClass":6:{s:5:"title";s:22:"NextGEN Importer Addon";s:4:"slug";s:23:"envira-nextgen-importer";s:7:"version";s:5:"1.2.2";s:5:"image";s:81:"https://enviragallery.com/wp-content/uploads/2015/10/nextgen-gallery-importer.png";s:7:"excerpt";s:103:"The NextGEN Importer addon allows you to import NextGEN galleries, albums and tags into Envira Gallery!";s:10:"categories";a:1:{i:0;s:4:"free";}}i:14;O:8:"stdClass":6:{s:5:"title";s:16:"Pagination Addon";s:4:"slug";s:17:"envira-pagination";s:7:"version";s:5:"1.6.8";s:5:"image";s:73:"https://enviragallery.com/wp-content/uploads/2015/10/pagination-addon.png";s:7:"excerpt";s:110:"The Pagination addon allows you to paginate gallery images across multiple pages - ideal for larger galleries.";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:15;O:8:"stdClass":6:{s:5:"title";s:25:"Password Protection Addon";s:4:"slug";s:26:"envira-password-protection";s:7:"version";s:5:"1.3.9";s:5:"image";s:76:"https://enviragallery.com/wp-content/uploads/2015/10/password-protection.png";s:7:"excerpt";s:135:"The password protection addon allows you to password protect your galleries. This is a perfect solution for exclusive client galleries!";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:16;O:8:"stdClass":6:{s:5:"title";s:14:"Printing Addon";s:4:"slug";s:15:"envira-printing";s:7:"version";s:5:"1.2.2";s:5:"image";s:93:"https://enviragallery.com/wp-content/uploads/2016/09/28dd50a0-6ed5-11e6-84c3-2694fee9a9d2.png";s:7:"excerpt";s:72:"Allow visitors to easily print photos from your WordPress image gallery.";s:10:"categories";a:1:{i:0;s:4:"free";}}i:17;O:8:"stdClass":6:{s:5:"title";s:14:"Proofing Addon";s:4:"slug";s:15:"envira-proofing";s:7:"version";s:5:"1.3.7";s:5:"image";s:71:"https://enviragallery.com/wp-content/uploads/2015/10/image-proofing.png";s:7:"excerpt";s:148:"Want to add a photo proofing process in your WordPress photo galleries? Envira adds photo proofing into WordPress to help with your client workflow.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:18;O:8:"stdClass":6:{s:5:"title";s:16:"Protection Addon";s:4:"slug";s:17:"envira-protection";s:7:"version";s:5:"1.3.3";s:5:"image";s:73:"https://enviragallery.com/wp-content/uploads/2015/10/protection-addon.png";s:7:"excerpt";s:203:"The Protection addon disables the ability for visitors to right click and download your images (both gallery display and lightbox views). It gives you complete freedom to display your work without worry.";s:10:"categories";a:1:{i:0;s:4:"free";}}i:19;O:8:"stdClass":6:{s:5:"title";s:14:"Schedule Addon";s:4:"slug";s:15:"envira-schedule";s:7:"version";s:5:"1.2.2";s:5:"image";s:71:"https://enviragallery.com/wp-content/uploads/2017/04/schedule_addon.png";s:7:"excerpt";s:153:"Want to display images at certain times? With Envira Gallery, you can schedule gallery images or entire galleries and albums for specific time intervals.";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:20;O:8:"stdClass":6:{s:5:"title";s:15:"Slideshow Addon";s:4:"slug";s:16:"envira-slideshow";s:7:"version";s:5:"1.3.4";s:5:"image";s:72:"https://enviragallery.com/wp-content/uploads/2015/10/slideshow-addon.png";s:7:"excerpt";s:135:"Turn your gallery lightbox view into a slideshow for your visitors. You can customize settings such as autoplaying and slideshow speed.";s:10:"categories";a:1:{i:0;s:4:"free";}}i:21;O:8:"stdClass":6:{s:5:"title";s:12:"Social Addon";s:4:"slug";s:13:"envira-social";s:7:"version";s:5:"1.5.6";s:5:"image";s:70:"https://enviragallery.com/wp-content/uploads/2015/10/socialsharing.png";s:7:"excerpt";s:157:"The Social Addon for Envira allows you to share your gallery images on all your favorite social networks like Facebook, Twitter, LinkedIn, WhatsApp and more!";s:10:"categories";a:2:{i:0;s:8:"advanced";i:1;s:5:"basic";}}i:22;O:8:"stdClass":6:{s:5:"title";s:20:"Standalone Galleries";s:4:"slug";s:17:"envira-standalone";s:7:"version";s:7:"1.1.3.1";s:5:"image";s:73:"https://enviragallery.com/wp-content/uploads/2015/10/standalone-addon.png";s:7:"excerpt";s:159:"The standalone addon allows you to access galleries created through the Envira post type with unique URLs. Now your galleries can have dedicated gallery pages!";s:10:"categories";a:1:{i:0;s:4:"free";}}i:23;O:8:"stdClass":6:{s:5:"title";s:25:"Supersize Lightbox Images";s:4:"slug";s:16:"envira-supersize";s:7:"version";s:5:"1.1.4";s:5:"image";s:72:"https://enviragallery.com/wp-content/uploads/2015/10/supersize-addon.png";s:7:"excerpt";s:148:"The Supersize addon allows you to display images at full scale when in lightbox mode. Enjoy breathtaking views of your photos on any viewing device.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:24;O:8:"stdClass":6:{s:5:"title";s:10:"Tags Addon";s:4:"slug";s:11:"envira-tags";s:7:"version";s:5:"1.7.5";s:5:"image";s:67:"https://enviragallery.com/wp-content/uploads/2015/10/tags-addon.png";s:7:"excerpt";s:220:"The Tags addon allows you to add custom tags to each of your images in your galleries. You can then query a custom gallery from tags used for your images, and you can even do animated tag filtering within your galleries.";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:25;O:8:"stdClass":6:{s:5:"title";s:12:"Videos Addon";s:4:"slug";s:13:"envira-videos";s:7:"version";s:5:"1.5.4";s:5:"image";s:69:"https://enviragallery.com/wp-content/uploads/2015/10/videos-addon.png";s:7:"excerpt";s:238:"The Envira Video Gallery plugin for WordPress allows you to create a beautiful video gallery from YouTube, Vimeo, Wistia, DailyMotion, Facebook, Instagram, Twitch, VideoPress and self-hosted videos. Create your video gallery in WordPress.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:26;O:8:"stdClass":6:{s:5:"title";s:18:"Watermarking Addon";s:4:"slug";s:19:"envira-watermarking";s:7:"version";s:5:"1.3.3";s:5:"image";s:72:"https://enviragallery.com/wp-content/uploads/2015/10/watermark-addon.png";s:7:"excerpt";s:61:"Protect your photos by adding custom watermarks in WordPress.";s:10:"categories";a:1:{i:0;s:5:"basic";}}i:27;O:8:"stdClass":6:{s:5:"title";s:17:"WooCommerce Addon";s:4:"slug";s:18:"envira-woocommerce";s:7:"version";s:5:"1.4.3";s:5:"image";s:74:"https://enviragallery.com/wp-content/uploads/2015/10/woocommerce-addon.png";s:7:"excerpt";s:148:"Do you want to sell your photos in WordPress? Envira gallery offers a complete integration with WooCommerce allowing you to easily sell your photos.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:28;O:8:"stdClass":6:{s:5:"title";s:18:"ZIP Importer Addon";s:4:"slug";s:19:"envira-zip-importer";s:7:"version";s:5:"1.2.1";s:5:"image";s:75:"https://enviragallery.com/wp-content/uploads/2015/10/zip-importer-addon.png";s:7:"excerpt";s:56:"Easily import photos from a .zip file into your gallery.";s:10:"categories";a:1:{i:0;s:8:"advanced";}}i:29;O:8:"stdClass":6:{s:5:"title";s:10:"Zoom Addon";s:4:"slug";s:11:"envira-zoom";s:7:"version";s:5:"1.3.6";s:5:"image";s:67:"https://enviragallery.com/wp-content/uploads/2016/05/zoom-addon.png";s:7:"excerpt";s:77:"Adds zoom functionality to images in your gallery to allow up close browsing.";s:10:"categories";a:1:{i:0;s:5:"basic";}}}', 'no'),
